html.js:
// JavaScript
function setViewCart(){ 
$('body').on('click', '.cart-btn', function(){
   setTimeout(function(){
    document.querySelectorAll('a[href="/bin/cart/view"]').forEach(function(val) {
      val.setAttribute('href', val.getAttribute('href') + "?t="+new Date().getTime());
    });
},100)

});
}
if( document.readyState === 'complete' ) {
  setViewCart();
} else {
   document.addEventListener('DOMContentLoaded', function () {
       setViewCart();
   });
}
