html.js:
// JavaScript
(function() {
    function AudioPlayer( btnId, switchId, audioPath ) {
        this.btn                = document.getElementById(btnId);
        this.switchCtn          = document.getElementById(switchId);
        this.soundwave          = this.btn.querySelector('.soundwave-active');
        this.snd                = new Audio(audioPath);
        this.animTimeout        = null;
        this.animModeOnRelease  = false; // Controls the animation mode for the "key" graphic
        this.releaseSwitchBound = this.releaseSwitch.bind(this);

        this.btn.addEventListener('mousedown', this.playAudio.bind(this));
        this.btn.addEventListener('touchstart', this.playAudio.bind(this));
        this.btn.addEventListener('keydown', this.playAudio.bind(this));
        this.switchCtn.addEventListener('mousedown', this.playAudio.bind(this));
        this.switchCtn.addEventListener('touchstart', this.playAudio.bind(this));
        this.switchCtn.addEventListener('keydown', this.playAudio.bind(this));

        this.soundwave.addEventListener('transitionend', function() {
            this.soundwave.classList.remove('playing');    
        }.bind(this));

        this.snd.load();
    }

    AudioPlayer.prototype.playAudio = function(e) {
        // if it's a keypress and it wasn't the spacebar or enter key, cancel
        if(e.type === 'keydown' && (e.keyCode !== 32 && e.keyCode !== 13)) {
            return;
        }

        // if it's already playing, just restart it
        if(this.snd.currentTime > 0 && this.snd.ended !== true) {
            this.snd.currentTime = 0;
        } else {
            this.snd.play();
        }

        this.triggerSwitchAnim();
        this.triggerSoundwaveAnim();
    };

    AudioPlayer.prototype.triggerSwitchAnim = function() {
        if(this.animTimeout !== null) {
            clearTimeout(this.animTimeout);
        }

        this.switchCtn.classList.add('down');

        // two different animation modes
        // 1. Switch goes back to "up" position automatically after a brief delay
        if(this.animModeOnRelease !== true) {
            this.animTimeout = setTimeout(function() {
                this.switchCtn.classList.remove('down');
            }.bind(this), 100);

        // 2. Switch only goes back to "up" position once user has stopped interacting with it
        } else {
            window.addEventListener('mouseup', this.releaseSwitchBound);
            window.addEventListener('touchend', this.releaseSwitchBound);
            window.addEventListener('keyup', this.releaseSwitchBound);
        }
    };

    AudioPlayer.prototype.releaseSwitch = function() {
        this.switchCtn.classList.remove('down');
        window.removeEventListener('mouseup', this.releaseSwitchBound);
        window.removeEventListener('touchend', this.releaseSwitchBound);
        window.removeEventListener('keyup', this.releaseSwitchBound);
    };

    AudioPlayer.prototype.triggerSoundwaveAnim = function() {
        this.soundwave.classList.remove('playing');
        setTimeout(function() {
            this.soundwave.classList.add('playing');    
        }.bind(this), 10);
    };


    // init
    new AudioPlayer( 'tactileAudioPlayer', 'tactileSwitch', '${asset.tactileSound}' );
    new AudioPlayer( 'linearAudioPlayer', 'linearSwitch', '${asset.linearSound}' );
    new AudioPlayer( 'blueAudioPlayer', 'blueSwitch', '${asset.blueSound}' );
})();
